import React, { useEffect, useState } from "react";
import "../stylesheets/App.scss";
import api from "../services/api";
import CharacterList from "./CharacterList";
import { Route, Switch } from "react-router-dom";

const App = () => {
  //state
  const [characters, setCharacters] = useState([]);
  const [filterText, setFilterText] = useState("");

  //api
  useEffect(() => {
    api.getDataFromApi().then((data) => {
      setCharacters(data); //guardo el array con todos los datos
    });
  }, []);

  //events
  const handleFilter = (filterText) => {
    setFilterText(filterText);
  };

  const filteredCharacters = characters.filter((character) => {
    return character.name.toLowerCase().includes(filterText.toLowerCase());
  });

  const renderDetail = (props) => {
    const routeCharacterId = props.match.params.productId;
    characters.find((character) => {
      if (routeCharacterId === character.id) {
        return true;
      } else {
        return false;
      }
    });
    return <h1>hola</h1>;
  };

  return (
    <div>
      <Switch>
        <Route exact path="/">
          <CharacterList
            characters={filteredCharacters}
            handleFilter={handleFilter}
          />
        </Route>
        <Route path="/character-detail/:productId" component={renderDetail} />
      </Switch>
    </div>
  );
};

export default App;
